CREATE TABLE IF NOT EXISTS associates (id INT AUTO_INCREMENT PRIMARY KEY,company_id INT NOT NULL,consultant_id INT NOT NULL,monthly_fee DECIMAL(10,2) NOT NULL DEFAULT 0,commission_rate DECIMAL(5,2) NOT NULL DEFAULT 10.00,start_date DATE NOT NULL,end_date DATE NULL,created_at DATETIME DEFAULT CURRENT_TIMESTAMP,CONSTRAINT fk_assoc_company FOREIGN KEY (company_id) REFERENCES companies(id) ON DELETE CASCADE,CONSTRAINT fk_assoc_user FOREIGN KEY (consultant_id) REFERENCES users(id) ON DELETE CASCADE,INDEX idx_assoc_period (start_date, end_date));