CREATE TABLE IF NOT EXISTS audit_logs (id BIGINT AUTO_INCREMENT PRIMARY KEY,user_id INT NULL,entity VARCHAR(60) NOT NULL,entity_id INT NOT NULL,action VARCHAR(60) NOT NULL,before_json JSON NULL,after_json JSON NULL,ip VARCHAR(64) NULL,user_agent VARCHAR(200) NULL,request_id CHAR(16) NULL,created_at DATETIME DEFAULT CURRENT_TIMESTAMP,INDEX idx_audit_entity (entity, entity_id),INDEX idx_audit_created (created_at));