CREATE TABLE IF NOT EXISTS companies (id INT AUTO_INCREMENT PRIMARY KEY,fantasy_name VARCHAR(160) NOT NULL,corporate_name VARCHAR(160),cnpj VARCHAR(18),ie VARCHAR(40),address VARCHAR(160),zip VARCHAR(12),city VARCHAR(100),state VARCHAR(2),phone VARCHAR(40),cel VARCHAR(40),whatsapp VARCHAR(40),email VARCHAR(160),instagram VARCHAR(160),business_activity VARCHAR(160),foundation_date VARCHAR(20),employees_qty INT,sector VARCHAR(100),accounting VARCHAR(160),referral VARCHAR(160),notes TEXT,status ENUM('pendente','ativo','inativo') NOT NULL DEFAULT 'pendente',inactivation_reason VARCHAR(255),approved_by INT,approved_at DATETIME,updated_by INT,created_at DATETIME DEFAULT CURRENT_TIMESTAMP,updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,INDEX idx_cnpj (cnpj),INDEX idx_status (status));